import Hobby from "./LayoutUI/Hobby";
import { HobbyListData } from "../../datas/HobbyListData"
import Button from "../Basics/Button";
import { useEffect, useState } from "react";


function HobbyList() {

    const [count, setCount] = useState(0)

    const countIncrement = (x) => {
        setCount( count + x)
    }

    // useEffect(() => {
    //     count()
    // }, [])

    const ContainerButton = ({count, countIncrement }) => {
        return (
            <Button title={count} onClick={() => countIncrement(1)} />
        )
    }

    return (
        <>
        <div className="flex flex-col grow gap-6">
            {HobbyListData.map((hobby, index) =>  <Hobby name={hobby.name} icon={hobby.icon} key={index} />)}
        </div>
        <p>{count}</p>
        <ContainerButton count={count} countIncrement={countIncrement} />
        </>

    );
}

export default HobbyList;
